/* smooth scrolling to sections */
function stickyMenu(){var t=$(".intro .back")
if(0!==t.length){var n=t.offset().top
window.addEventListener("scroll",function(){var e=$(window).scrollTop(),o=n-e
e>=n&&0>=o?t.addClass("fix-top"):t.removeClass("fix-top")})}}$(function(){$("a[href*=#]:not([href=#])").click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var t=$(this.hash)
if(t=t.length?t:$("[name="+this.hash.slice(1)+"]"),t.length)return $("html,body").animate({scrollTop:t.offset().top-50},1e3),!1}})}),$(document).ready(function(){stickyMenu()})
